import React, { useState } from 'react';
import { Box, Paper, Typography, IconButton, Button, Avatar, useTheme, useMediaQuery } from '@mui/material';
import ChevronLeftIcon from '@mui/icons-material/ChevronLeft';
import ChevronRightIcon from '@mui/icons-material/ChevronRight';
import { motion, AnimatePresence } from 'framer-motion';

const cards = [
  {
    title: '初心者向けドライバー',
    desc: 'やさしく飛ばせる設計。AIマッチ度92%。',
    img: '/sample/driver.png',
    tags: ['#初心者', '#飛距離アップ'],
    cta: '詳細を見る',
  },
  {
    title: '安定型アイアン',
    desc: '方向性と安定感を両立。AIマッチ度88%。',
    img: '/sample/iron.png',
    tags: ['#安定性', '#方向性'],
    cta: '詳細を見る',
  },
  {
    title: '高コスパユーティリティ',
    desc: 'コスパ重視の万能クラブ。AIマッチ度85%。',
    img: '/sample/utility.png',
    tags: ['#コスパ', '#万能'],
    cta: '詳細を見る',
  },
];

export default function AppleLikeCarousel() {
  const [index, setIndex] = useState(0);
  const [direction, setDirection] = useState(0); // -1=左, 1=右
  const theme = useTheme();
  const isMd = useMediaQuery(theme.breakpoints.up('md'));
  const isSm = useMediaQuery(theme.breakpoints.up('sm'));
  const slideWidth = isMd ? 420 : isSm ? 320 : 260;
  const perspective = 1000;

  const handlePrev = () => {
    setDirection(-1);
    setIndex(i => Math.max(0, i - 1));
  };
  const handleNext = () => {
    setDirection(1);
    setIndex(i => Math.min(cards.length - 1, i + 1));
  };

  // ドットインジケーター
  const renderDots = () => (
    <Box sx={{ display: 'flex', justifyContent: 'center', mt: 3, gap: 1 }}>
      {cards.map((_, i) => (
        <Box key={i} sx={{
          width: 9, height: 9, borderRadius: '50%',
          bgcolor: i === index ? '#222' : '#E3E6EA',
          transition: 'all 0.3s',
          boxShadow: i === index ? '0 2px 8px rgba(0,0,0,0.10)' : 'none',
        }} />
      ))}
    </Box>
  );

  return (
    <Box 
      sx={{ 
        position: 'relative', 
        width: '100%', 
        maxWidth: 900, 
        mx: 'auto', 
        py: { xs: 3, md: 7 },
        display: 'flex', 
        alignItems: 'center', 
        justifyContent: 'center',
        minHeight: { xs: 420, md: 540 },
        perspective: `${perspective}px`,
        '&:hover .carousel-nav-btn': { opacity: 1 }
      }}
    >
      {/* カルーセル本体 */}
      <Box sx={{ 
        display: 'flex', 
        alignItems: 'center', 
        justifyContent: 'center', 
        position: 'relative', 
        minHeight: { xs: 320, md: 420 },
        width: '100%',
      }}>
        {/* 前へボタン */}
        <IconButton 
          onClick={handlePrev} 
          disabled={index === 0} 
          className="carousel-nav-btn"
          sx={{ 
            zIndex: 3, mr: 1, opacity: 0, transition: 'opacity 0.2s',
            width: 48, height: 48, borderRadius: '50%',
            background: 'rgba(255,255,255,0.92)',
            boxShadow: '0 4px 12px rgba(0,0,0,0.10)',
            '&:hover': { bgcolor: '#f5f5f7', opacity: 1 },
            color: '#888',
          }}
        >
          <ChevronLeftIcon fontSize="large" />
        </IconButton>
        {/* カード群 */}
        <Box sx={{
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          width: slideWidth * 1.8,
          position: 'relative',
          perspective: `${perspective}px`,
        }}>
          {/* サイドカード（左） */}
          {index > 0 && (
            <motion.div
              key={`left-${index-1}`}
              initial={{ opacity: 0, x: -80, scale: 0.85, filter: 'blur(2px)' }}
              animate={{ opacity: 0.6, x: -slideWidth * 0.6, scale: 0.85, filter: 'blur(2px)' }}
              exit={{ opacity: 0, x: -120, scale: 0.85, filter: 'blur(2px)' }}
              transition={{ duration: 0.6, ease: [0.25,0.46,0.45,0.94] }}
              style={{ position: 'absolute', left: '50%', top: 0, zIndex: 1, width: slideWidth, pointerEvents: 'none' }}
            >
              <Paper elevation={0} sx={{ p: { xs: 1, md: 2 }, borderRadius: 5, textAlign: 'center', opacity: 0.7, boxShadow: '0 2px 12px rgba(0,0,0,0.04)' }}>
                <Avatar src={cards[index - 1].img} alt={cards[index - 1].title} sx={{ width: { xs: 38, md: 54 }, height: { xs: 38, md: 54 }, mx: 'auto', mb: 0.5, bgcolor: '#e9eaec' }} />
                <Typography variant="subtitle2" sx={{ color: 'text.secondary', fontWeight: 500, fontSize: { xs: 13, md: 15 } }}>{cards[index - 1].title}</Typography>
              </Paper>
            </motion.div>
          )}
          {/* メインカード */}
          <AnimatePresence initial={false} custom={direction} mode="wait">
            <motion.div
              key={index}
              custom={direction}
              initial={{ opacity: 0, x: direction > 0 ? 120 : -120, scale: 0.98, zIndex: 2 }}
              animate={{ opacity: 1, x: 0, scale: 1.1, zIndex: 2, boxShadow: '0 12px 32px rgba(0,0,0,0.15)', filter: 'none' }}
              exit={{ opacity: 0, x: direction > 0 ? -120 : 120, scale: 0.98, zIndex: 1 }}
              transition={{ duration: 0.6, ease: [0.25,0.46,0.45,0.94] }}
              style={{ position: 'absolute', left: '50%', top: 0, width: slideWidth, transform: 'translateX(-50%)', zIndex: 2 }}
            >
              <Paper elevation={0} sx={{ 
                p: { xs: 3, md: 6 }, 
                borderRadius: { xs: 5, md: 7 }, 
                minWidth: { xs: 260, md: 380 }, 
                maxWidth: { xs: 380, md: 480 }, 
                mx: { xs: 1, md: 3 }, 
                textAlign: 'center', 
                boxShadow: '0 8px 32px 0 rgba(0,0,0,0.10), 0 1.5px 8px 0 rgba(0,0,0,0.04)',
                display: 'flex', flexDirection: 'column', alignItems: 'center',
              }}>
                <Avatar src={cards[index].img} alt={cards[index].title} sx={{ width: { xs: 70, md: 110 }, height: { xs: 70, md: 110 }, mx: 'auto', mb: { xs: 2, md: 3 }, boxShadow: 2, bgcolor: '#e9eaec' }} />
                <Typography variant="h5" sx={{ fontWeight: 800, mb: { xs: 1, md: 2 }, fontSize: { xs: 22, md: 28 }, color: '#111' }}>{cards[index].title}</Typography>
                <Typography sx={{ color: 'text.secondary', mb: { xs: 2, md: 3 }, fontSize: { xs: 15, md: 17 }, fontWeight: 400 }}>{cards[index].desc}</Typography>
                <Box sx={{ mb: { xs: 2, md: 3 }, display: 'flex', flexWrap: 'wrap', gap: 1, justifyContent: 'center' }}>
                  {cards[index].tags.map(tag => (
                    <Paper key={tag} sx={{ display: 'inline-block', px: 1.7, py: 0.6, borderRadius: 2, bgcolor: '#f5f7fa', color: '#222', fontWeight: 600, fontSize: 13, boxShadow: 'none', letterSpacing: 0.2 }}>
                      {tag}
                    </Paper>
                  ))}
                </Box>
                <Button variant="contained" sx={{
                  fontWeight: 700,
                  borderRadius: 3,
                  px: { xs: 3, md: 5 },
                  py: { xs: 1, md: 1.5 },
                  boxShadow: 2,
                  background: '#111',
                  color: '#fff',
                  fontSize: { xs: 15, md: 18 },
                  mt: { xs: 1, md: 2 },
                  transition: 'all 0.18s',
                  '&:hover': { background: '#222', color: '#fff', transform: 'scale(1.04)', boxShadow: 4 }
                }}>
                  {cards[index].cta}
                </Button>
              </Paper>
            </motion.div>
          </AnimatePresence>
          {/* サイドカード（右） */}
          {index < cards.length - 1 && (
            <motion.div
              key={`right-${index+1}`}
              initial={{ opacity: 0, x: 80, scale: 0.85, filter: 'blur(2px)' }}
              animate={{ opacity: 0.6, x: slideWidth * 0.6, scale: 0.85, filter: 'blur(2px)' }}
              exit={{ opacity: 0, x: 120, scale: 0.85, filter: 'blur(2px)' }}
              transition={{ duration: 0.6, ease: [0.25,0.46,0.45,0.94] }}
              style={{ position: 'absolute', left: '50%', top: 0, zIndex: 1, width: slideWidth, pointerEvents: 'none' }}
            >
              <Paper elevation={0} sx={{ p: { xs: 1, md: 2 }, borderRadius: 5, textAlign: 'center', opacity: 0.7, boxShadow: '0 2px 12px rgba(0,0,0,0.04)' }}>
                <Avatar src={cards[index + 1].img} alt={cards[index + 1].title} sx={{ width: { xs: 38, md: 54 }, height: { xs: 38, md: 54 }, mx: 'auto', mb: 0.5, bgcolor: '#e9eaec' }} />
                <Typography variant="subtitle2" sx={{ color: 'text.secondary', fontWeight: 500, fontSize: { xs: 13, md: 15 } }}>{cards[index + 1].title}</Typography>
              </Paper>
            </motion.div>
          )}
        </Box>
        {/* 次へボタン */}
        <IconButton 
          onClick={handleNext} 
          disabled={index === cards.length - 1} 
          className="carousel-nav-btn"
          sx={{ 
            zIndex: 3, ml: 1, opacity: 0, transition: 'opacity 0.2s',
            width: 48, height: 48, borderRadius: '50%',
            background: 'rgba(255,255,255,0.92)',
            boxShadow: '0 4px 12px rgba(0,0,0,0.10)',
            '&:hover': { bgcolor: '#f5f5f7', opacity: 1 },
            color: '#888',
          }}
        >
          <ChevronRightIcon fontSize="large" />
        </IconButton>
      </Box>
      {/* ドットインジケーター */}
      {renderDots()}
    </Box>
  );
}
