    scrollToBottom();
  }, [messages]);

  // isTypingがfalseになったらフォーカスを当てる
  useEffect(() => {
    if (!isTyping && inputRef.current) {
      inputRef.current.focus();
    }
  }, [isTyping]);

  const handleSend = () => {
    let sendText = inputMessage.trim();
    if (!sendText) return;

    const newMessage = {
      message: sendText,
      direction: 'outgoing',
      sender: 'user',
      timestamp: new Date().toISOString()
    };
    setMessages(prev => [...prev, newMessage]);
    setInputMessage('');
    setIsTyping(true);
    setClubSetsLoading(true);
    setClubSetsError(null);
    const fetchClubSets = async () => {
      try {
        const userData = {
          golf_history: '3年',
          score: 100,
          main_club: '7番アイアン',
          improvement_point: 'スコア改善'
        };
        const sets = await getClubSetRecommendations(userData);
        sets.sort((a, b) => b.match - a.match);
        setClubSets(sets);
      } catch (err) {
        setClubSetsError('クラブセットの検索に失敗しました');
      } finally {
        setTimeout(() => {
          const botResponse = {
            message: getNextQuestion(sendText),
            sender: "bot",
            direction: "incoming",
            timestamp: new Date().toISOString()
          };
          setMessages(prev => [...prev, botResponse]);
          updateRecommendation(sendText);
          setIsTyping(false);
          setClubSetsLoading(false);
          inputRef.current?.focus();
        }, 800);
      }
    };
    fetchClubSets();
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSend();
    }
  };

