            {isMobile ? (
              <Box sx={{ width: '100vw', minHeight: '100vh', bgcolor: '#fff', pb: 8 }}>
                {/* ヘッダー */}
                <Box sx={{ position: 'fixed', top: 0, left: 0, width: '100%', height: 56, bgcolor: '#fff', borderBottom: '1px solid #e0e0e2', display: 'flex', alignItems: 'center', justifyContent: 'space-between', px: 3, zIndex: 1200 }}>
                  <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                    <Box sx={{ width: 56, height: 56, bgcolor: '#222', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
                      <GolfCourseIcon sx={{ color: '#fff', fontSize: 28 }} />
                    </Box>
                    <Typography sx={{ fontWeight: 700, fontSize: 18, color: '#222' }}>
