                </Box>
                {/* メイン */}
                <Box sx={{ pt: 7, pb: 10 }}>
                  {/* チャット画面 */}
                  {!showChat ? (
                    <Box sx={{
                      flex: 1,
                      display: 'flex',
                      flexDirection: 'column',
                      alignItems: 'center',
                      justifyContent: 'center',
                      width: '100%',
                      height: '100%',
