        <Grid item xs={12} md={5}>
          <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
            {[1, 2, 3].map(i => (
              <Paper
                key={i}
                elevation={2}
                sx={{
                  p: 2,
                  borderRadius: 3,
                  boxShadow: theme.shadows[2],
                  cursor: 'pointer',
                  transition: theme.transitions.create('transform'),
                  '&:hover': { transform: 'scale(1.04)', boxShadow: theme.shadows[6] },
                  backgroundColor: theme.palette.background.paper + ' !important',
                  color: theme.palette.text.primary + ' !important',
                }}
                onClick={() => setExpandedCard(i)}
              >
                <Typography sx={{ fontWeight: 700, color: theme.palette.primary.main }}>
                  代替オプション #{i}
                </Typography>
                <Typography sx={{ color: theme.palette.secondary.main, fontWeight: 600 }}>
                  マッチ度 {85 - i * 5}%
                </Typography>
              </Paper>
            ))}
          </Box>
        </Grid>
      </Grid>
      {/* 拡張カード（モーダル風） */}
      {expandedCard && (
        <Box
          sx={{
            position: 'fixed',
            top: 0, left: 0, width: '100vw', height: '100vh',
            bgcolor: 'rgba(0,0,0,0.25)',
            zIndex: 2000,
            display: 'flex', alignItems: 'center', justifyContent: 'center'
          }}
          onClick={() => setExpandedCard(null)}
        >
          <Paper sx={{ p: 4, borderRadius: 4, minWidth: 320, maxWidth: 480, position: 'relative' }}>
            <IconButton
              onClick={() => setExpandedCard(null)}
              sx={{ position: 'absolute', top: 8, right: 8 }}
            >
              <CloseIcon />
            </IconButton>
            <Typography variant="h6" sx={{ fontWeight: 700, mb: 2 }}>
              代替オプション #{expandedCard} の詳細
            </Typography>
            {/* ここに詳細情報や動画・レビュー等を追加 */}
            <Typography>ここに詳細情報や動画・レビュー等を追加</Typography>
          </Paper>
        </Box>
      )}
    </Box>
  );
}

export default App;
