import React, { useState } from 'react';
import { Box, Paper, Typography, IconButton, Button, Avatar, useTheme, useMediaQuery } from '@mui/material';
import ChevronLeftIcon from '@mui/icons-material/ChevronLeft';
import ChevronRightIcon from '@mui/icons-material/ChevronRight';
import { motion, AnimatePresence } from 'framer-motion';

const cards = [
  {
    title: '初心者向けドライバー',
    desc: 'やさしく飛ばせる設計。AIマッチ度92%。',
    img: '/sample/driver.png',
    tags: ['#初心者', '#飛距離アップ'],
    cta: '詳細を見る',
  },
  {
    title: '安定型アイアン',
    desc: '方向性と安定感を両立。AIマッチ度88%。',
    img: '/sample/iron.png',
    tags: ['#安定性', '#方向性'],
    cta: '詳細を見る',
  },
  {
    title: '高コスパユーティリティ',
    desc: 'コスパ重視の万能クラブ。AIマッチ度85%。',
    img: '/sample/utility.png',
    tags: ['#コスパ', '#万能'],
    cta: '詳細を見る',
  },
];

export default function AppleLikeCarousel() {
  const [index, setIndex] = useState(0);
  const [direction, setDirection] = useState(0); // スライド方向: -1=左, 1=右

  const handlePrev = () => {
    setDirection(-1);
    setIndex(i => Math.max(0, i - 1));
  };
  const handleNext = () => {
    setDirection(1);
    setIndex(i => Math.min(cards.length - 1, i + 1));
  };

  return (
    <Box sx={{ position: 'relative', width: '100%', maxWidth: 900, mx: 'auto', py: 4, '&:hover .carousel-nav-btn': { opacity: 1 } }}>
      {/* カルーセル本体 */}
      <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'center', position: 'relative', minHeight: 340 }}>
        {/* 前へボタン */}
        <IconButton onClick={handlePrev} disabled={index === 0} className="carousel-nav-btn" sx={{ zIndex: 2, mr: 1, bgcolor: '#fff', boxShadow: 2, opacity: 0, transition: 'opacity 0.2s', '&:hover': { bgcolor: 'primary.light', opacity: 1 } }}>
          <ChevronLeftIcon fontSize="large" />
        </IconButton>

        {/* 左端カード（チラ見せ） */}
        <Box sx={{ width: 180, opacity: index > 0 ? 1 : 0, transform: index > 0 ? 'scale(0.92)' : 'scale(0.85)', transition: 'all 0.3s', mx: -3, zIndex: 1 }}>
          {index > 0 && (
            <Paper elevation={2} sx={{ p: 2, borderRadius: 4, textAlign: 'center', opacity: 0.7 }}>
              <Avatar src={cards[index - 1].img} alt={cards[index - 1].title} sx={{ width: 60, height: 60, mx: 'auto', mb: 1 }} />
              <Typography variant="subtitle2" sx={{ color: 'text.secondary', fontWeight: 500 }}>{cards[index - 1].title}</Typography>
            </Paper>
          )}
        </Box>

        {/* メインカード */}
        <AnimatePresence initial={false} mode="wait">
          <motion.div
            key={index}
            initial={{ opacity: 0, scale: 0.96, x: direction > 0 ? 40 : -40 }}
            animate={{ opacity: 1, scale: 1, x: 0 }}
            exit={{ opacity: 0, scale: 0.96, x: direction > 0 ? -40 : 40 }}
            transition={{ duration: 0.38, ease: 'easeInOut' }}
            style={{ zIndex: 2 }}
          >
            <Paper elevation={6} sx={{ p: 4, borderRadius: 6, minWidth: 320, maxWidth: 420, mx: 2, textAlign: 'center', boxShadow: 6 }}>
              <Avatar src={cards[index].img} alt={cards[index].title} sx={{ width: 100, height: 100, mx: 'auto', mb: 2, boxShadow: 3 }} />
              <Typography variant="h5" sx={{ fontWeight: 800, mb: 1 }}>{cards[index].title}</Typography>
              <Typography sx={{ color: 'text.secondary', mb: 2 }}>{cards[index].desc}</Typography>
              <Box sx={{ mb: 2 }}>
                {cards[index].tags.map(tag => (
                  <Paper key={tag} sx={{ display: 'inline-block', px: 1.5, py: 0.5, mr: 1, mb: 0.5, borderRadius: 2, bgcolor: '#E3F2FD', color: '#1976D2', fontWeight: 600, fontSize: 13 }}>
                    {tag}
                  </Paper>
                ))}
              </Box>
              <Button variant="contained" sx={{
                fontWeight: 700,
                borderRadius: 3,
                px: 4,
                py: 1.2,
                boxShadow: 2,
                background: '#111',
                color: '#fff',
                transition: 'all 0.18s',
                '&:hover': { background: '#222', color: '#fff', transform: 'scale(1.04)', boxShadow: 4 }
              }}>
                {cards[index].cta}
              </Button>
            </Paper>
          </motion.div>
        </AnimatePresence>

        {/* 右端カード（チラ見せ） */}
        <Box sx={{ width: 180, opacity: index < cards.length - 1 ? 1 : 0, transform: index < cards.length - 1 ? 'scale(0.92)' : 'scale(0.85)', transition: 'all 0.3s', mx: -3, zIndex: 1 }}>
          {index < cards.length - 1 && (
            <Paper elevation={2} sx={{ p: 2, borderRadius: 4, textAlign: 'center', opacity: 0.7 }}>
              <Avatar src={cards[index + 1].img} alt={cards[index + 1].title} sx={{ width: 60, height: 60, mx: 'auto', mb: 1 }} />
              <Typography variant="subtitle2" sx={{ color: 'text.secondary', fontWeight: 500 }}>{cards[index + 1].title}</Typography>
            </Paper>
          )}
        </Box>

        {/* 次へボタン */}
        <IconButton onClick={handleNext} disabled={index === cards.length - 1} className="carousel-nav-btn" sx={{ zIndex: 2, ml: 1, bgcolor: '#fff', boxShadow: 2, opacity: 0, transition: 'opacity 0.2s', '&:hover': { bgcolor: 'primary.light', opacity: 1 } }}>
          <ChevronRightIcon fontSize="large" />
        </IconButton>
      </Box>
    </Box>
  );
}
