            >
              <SettingsIcon sx={{ fontSize: 19, mb: 0.5, transition: 'color 0.18s' }} />
              <span style={{ fontWeight: 500, fontSize: 11 }}>設定</span>
</Box>
            <Box
              sx={{
                display: 'flex', flexDirection: 'column', alignItems: 'center', gap: 0.5, width: '100%', py: 1,
                cursor: 'pointer',
                transition: 'all 0.18s cubic-bezier(.4,2,.6,1)',
                bgcolor: selectedNav === 'login' ? '#F7F9FB' : 'transparent',
                color: selectedNav === 'login' ? '#111' : '#222',
                fontWeight: selectedNav === 'login' ? 700 : 400,
                boxShadow: selectedNav === 'login' ? 2 : 0,
                '& svg': { color: selectedNav === 'login' ? '#111' : '#222' }
              }}
              tabIndex={0}
              role="button"
              onClick={() => setSelectedNav('login')}
            >
              <LoginIcon sx={{ fontSize: 19, mb: 0.5, transition: 'color 0.18s' }} />
              <span style={{ fontWeight: 500, fontSize: 11 }}>ログイン</span>
            </Box>
          </Box>
        </Box>
        {/* 中央カラム */}
        <Box sx={{
          width: { md: 520 },
          flexShrink: 0,
          display: 'flex',
          flexDirection: 'column',
          height: { md: 'calc(100vh - 56px - 32px)', xs: 'calc(100vh - 56px)' },
          overflow: 'hidden',
          borderRight: '1px solid #E3E6EA',
          position: 'relative',
          alignItems: 'center',
          justifyContent: 'center',
          bgcolor: '#fff',
          m: { md: 2, xs: 0 },
          boxShadow: { md: 3, xs: 0 },
          borderRadius: { md: '18px', xs: 0 },
          transition: 'all 0.25s',
          boxSizing: 'border-box',
        }}>
          {/* リセットボタンを右上に絶対配置 */}
          <Tooltip title="リセット">
            <IconButton
              onClick={handleResetChat}
              sx={{
                position: 'absolute',
                top: 16,
                right: 16,
                zIndex: 20,
                bgcolor: 'transparent',
                color: '#888',
                border: '2.5px solid #B0B0B0',
                boxShadow: '0 2px 8px rgba(0,0,0,0.12)',
                width: 44,
                height: 44,
                opacity: 0.6,
                transition: 'opacity 0.2s, background 0.2s, color 0.2s, border-color 0.2s, box-shadow 0.2s',
                borderRadius: '50%',
                '&:hover': {
                  opacity: 1,
                  bgcolor: '#222',
                  color: '#fff',
                  borderColor: '#222',
                  boxShadow: 8,
                },
              }}
            >
              <RestartAltIcon sx={{ fontSize: 24, color: '#fff' }} />
            </IconButton>
          </Tooltip>
          {/* チャット履歴エリア（下部に選択肢バー分の余白） */}
          <Box sx={{ flex: 1, width: '100%', overflowY: 'auto', p: 2, pb: '96px' }}>
            {chatFlow.slice(0, step + 1).map((q, idx) => (
              <Box key={idx} sx={{ display: 'flex', flexDirection: 'column', alignItems: 'stretch', mb: 2 }}>
                {/* AIの質問バブル（左側）＋アバター */}
                <Box sx={{ display: 'flex', flexDirection: 'row', alignItems: 'flex-end', mb: 0.5 }}>
                  <Avatar sx={{ bgcolor: '#fff', color: '#222', border: '1.5px solid #E3E6EA', width: 32, height: 32, mr: 1 }}>
                    <SmartToyIcon sx={{ fontSize: 20 }} />
                  </Avatar>
                  <Paper sx={{
                    display: 'inline-block',
                    width: 'fit-content',
                    maxWidth: '60%',
                    py: 1.2,
                    px: 2.5,
                    borderRadius: '18px',
                    bgcolor: '#fff',
                    color: '#222',
                    border: '1.5px solid #E3E6EA',
                    boxShadow: 1,
                    fontWeight: 500,
                    fontSize: 15,
                    lineHeight: 1.5,
                    letterSpacing: 0.2,
                    whiteSpace: 'pre-wrap',
                    wordBreak: 'break-word',
                  }}>
                    {q.question}
                  </Paper>
                </Box>
                {/* ユーザー側（右側）領域＋アバター */}
                <Box sx={{ display: 'flex', flexDirection: 'row', justifyContent: 'flex-end', alignItems: 'flex-end', minHeight: 36 }}>
                  {answers[idx] && (
                    <>
                      <Paper sx={{
                        display: 'inline-block',
                        maxWidth: '60%',
                        width: 'fit-content',
                        py: 1.2,
                        px: 2.5,
                        borderRadius: '18px',
                        bgcolor: '#222',
                        color: '#fff',
                        border: '1.5px solid #222',
                        boxShadow: 1,
                        fontWeight: 400,
                        fontSize: 15,
                        animation: `${fadeIn} 0.5s`,
                        lineHeight: 1.5,
                        letterSpacing: 0.2,
                        whiteSpace: 'pre-wrap',
                        wordBreak: 'break-word',
                        mr: 1
                      }}>
                        {answers[idx]}
                      </Paper>
                      {userAvatar}
                    </>
                  )}
                </Box>
              </Box>
            ))}
            {/* スクロール参照要素を追加 */}
            <div ref={messagesEndRef} style={{ float: 'left', clear: 'both' }} />
          </Box>
          {/* 選択肢バー（下部固定） or 診断完了メッセージ */}
          {(chatFlow[step] && !answers[step]) ? (
            <Box
              ref={optionsBarRef}
              sx={{
                position: 'absolute',
                left: 0,
                right: 0,
                bottom: 0,
                width: '100%',
                display: 'flex',
                flexWrap: 'wrap',
                justifyContent: 'center',
                alignItems: 'flex-start',
                gap: 1.2,
                px: 2,
                py: 2,
                zIndex: 10,
                background: '#fff',
                borderTop: '1px solid #eee',
                minHeight: 120,
                maxHeight: 120,
                alignItems: 'flex-start'
              }}
            >
              {chatFlow[step].options.map((opt) => (
                <Button
                  key={opt}
                  onClick={() => handleOptionSelect(opt)}
                  variant="contained"
                  sx={{
                    borderRadius: '18px',
                    px: 2.5,
                    py: 0.9,
                    fontWeight: 500,
                    fontSize: 15,
                    minWidth: 100,
                    boxShadow: 2,
                    background: '#111',
                    color: '#fff',
                    transition: 'all 0.18s cubic-bezier(.4,2,.6,1)',
                    letterSpacing: 0.5,
                    '&:hover': {
                      background: '#222',
                      color: '#fff',
                      boxShadow: 4,
                      transform: 'scale(1.06)',
                    },
                    '&:focus': {
                      outline: `2px solid #111`,
                      outlineOffset: 2
                    }
                  }}
                >
                  {opt}
                </Button>
              ))}
            </Box>
          ) : (
            answers.length === chatFlow.length && (
              <Box
                sx={{
                  position: 'absolute',
                  left: 0,
                  right: 0,
                  bottom: 0,
                  width: '100%',
                  minHeight: 120,
                  maxHeight: 120,
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  fontWeight: 600,
                  fontSize: 18,
                  color: '#222',
                  letterSpacing: 1,
                  background: '#fff',
                  borderTop: '1px solid #eee'
                }}
              >
                診断が完了しました
              </Box>
            )
          )}
        </Box>
        {/* 右カラム */}
<Box
sx={{
            flex: 1,
            minWidth: 0,
            display: 'flex',
            flexDirection: 'column',
            height: 'calc(100vh - 56px)',
            overflow: 'hidden',
            bgcolor: '#F7F9FB',
            borderLeft: '1px solid #E3E6EA'
}}
>
<Box
sx={{
            height: '100%',
              overflowY: 'auto',
              boxSizing: 'border-box',
              '&::-webkit-scrollbar': {
                width: '8px',
                background: '#E3E6EA'
              },
              '&::-webkit-scrollbar-thumb': {
                backgroundColor: '#B0C4DE',
                borderRadius: '4px'
              }
            }}
          >
            {/* ClubRecommendationsを表示 */}
            <ClubRecommendations />
          </Box>
</Box>
</Box>
</ThemeProvider>
);
}

export default App;
