import React, { useState, useRef, useEffect } from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import Box from '@mui/material/Box';
import Paper from '@mui/material/Paper';
import useMediaQuery from '@mui/material/useMediaQuery';
import GolfCourseIcon from '@mui/icons-material/GolfCourse';
import Avatar from '@mui/material/Avatar';
import SmartToyIcon from '@mui/icons-material/SmartToy';
import HistoryIcon from '@mui/icons-material/History';
import SettingsIcon from '@mui/icons-material/Settings';
import LoginIcon from '@mui/icons-material/Login';
import Divider from '@mui/material/Divider';
import RestartAltIcon from '@mui/icons-material/RestartAlt';
import Tooltip from '@mui/material/Tooltip';
import Fade from '@mui/material/Fade';
import { getClubSetRecommendations } from './api/client';
import { keyframes } from '@mui/system';
import CircularProgress from '@mui/material/CircularProgress';
import TextField from '@mui/material/TextField';
import IconButton from '@mui/material/IconButton';
import SendIcon from '@mui/icons-material/Send';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import StarIcon from '@mui/icons-material/Star';
import StarBorderIcon from '@mui/icons-material/StarBorder';
import ChevronLeftIcon from '@mui/icons-material/ChevronLeft';
import ChevronRightIcon from '@mui/icons-material/ChevronRight';
import { motion, AnimatePresence } from 'framer-motion';
import { useTheme } from '@mui/material/styles';
import Grid from '@mui/material/Grid';
import Chip from '@mui/material/Chip';
import Rating from '@mui/material/Rating';
import ShareIcon from '@mui/icons-material/Share';
import CloseIcon from '@mui/icons-material/Close';
import InfoOutlinedIcon from '@mui/icons-material/InfoOutlined';
import ClubRecommendations from './components/ClubRecommendations';
import { Tabs, Tab, LinearProgress } from '@mui/material';
import MoreVertIcon from '@mui/icons-material/MoreVert';
import Menu from '@mui/material/Menu';
import MenuItem from '@mui/material/MenuItem';
import ArrowBackIosNewIcon from '@mui/icons-material/ArrowBackIosNew';
import TwitterIcon from '@mui/icons-material/Twitter';
import { styled } from '@mui/material/styles';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Terms from './components/Terms';
import Privacy from './components/Privacy';
import MenuIcon from '@mui/icons-material/Menu';
import Drawer from '@mui/material/Drawer';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import HomeIcon from '@mui/icons-material/Home';
import DescriptionIcon from '@mui/icons-material/Description';
import PolicyIcon from '@mui/icons-material/Policy';
import Link from '@mui/material/Link';

// 繧｢繝九Γ繝ｼ繧ｷ繝ｧ繝ｳ縺ｮ螳夂ｾｩ
const fadeIn = keyframes`
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
`;

const fadeOut = keyframes`
  from {
    opacity: 1;
  }
  to {
    opacity: 0;
  }
`;

// 繝峨ャ繝医い繝九Γ繝ｼ繧ｷ繝ｧ繝ｳ縺ｮ螳夂ｾｩ
const dotFlashing = keyframes`
  0% { opacity: 0.2; }
  20% { opacity: 1; }
  100% { opacity: 0.2; }
`;

const theme = createTheme({
  breakpoints: {
    values: {
      xs: 0,
      sm: 600,
      md: 900,
      lg: 1200, // PC/SP切り替えポイント
      xl: 1536
    }
  },
  palette: {
    primary: {
      main: '#222', // Apple風ニュートラル
      light: '#E3E6EA',
      dark: '#888',
      contrastText: '#fff',
    },
    background: {
      default: '#F7F9FB',
      paper: '#fff',
    },
    text: {
      primary: '#222',
      secondary: '#555',
      disabled: '#B0B0B0',
    },
    divider: '#E3E6EA',
  },
  components: {
    MuiButton: {
      styleOverrides: {
        root: {
fontWeight: 600,
          textTransform: 'none',
          '&:hover': {
            boxShadow: '0 2px 4px rgba(0,0,0,0.2)'
          }
        },
        containedPrimary: {
          background: 'linear-gradient(45deg, #1976D2 0%, #2196F3 100%)',
          '&:hover': {
            background: 'linear-gradient(45deg, #1565C0 0%, #1976D2 100%)'
          }
        }
      }
    },
    MuiPaper: {
      styleOverrides: {
        root: {
          border: '1px solid #E0E0E0',
          boxShadow: '0 2px 8px rgba(0,0,0,0.1)'
        }
      }
    },
    MuiAppBar: {
      styleOverrides: {
        root: {
          background: 'linear-gradient(45deg, #1976D2 0%, #2196F3 100%)',
          boxShadow: '0 2px 4px rgba(0,0,0,0.2)'
        }
      }
    },
    MuiLinearProgress: {
      styleOverrides: {
        root: {
          backgroundColor: '#E3F2FD'
        },
        bar: {
          backgroundColor: '#1976D2'
        }
      }
    },
    MuiRadio: {
      styleOverrides: {
        root: {
          color: '#90CAF9',
          '&.Mui-checked': {
            color: '#1976D2'
          }
        }
      }
    }
  },
  typography: {
    fontFamily: [
      'SF Pro Display',
      'Noto Sans JP',
      'system-ui',
      'Arial',
      'sans-serif'
    ].join(','),
    h6: {
      fontWeight: 700,
      color: '#222',
    },
    body1: {
      lineHeight: 1.6
    }
  }
});

const initialRecommendation = {
  driver: '初心者向けドライバー',
  iron: 'ゲーム改善用アイアン',
  putter: 'ストローク安定型パター'
};

// デフォルトのカスタムセット4つ（14本クラブ情報付き）
const defaultClubSets = [
  {
    set_name: 'カスタムセットA',
    match: 90,
    clubs: [
      { type: 'ドライバー', brand: 'NIKE', model: 'VR Pro', flex: 'S' },
      { type: '3W', brand: 'NIKE', model: 'VR Pro', flex: 'S' },
      { type: '5W', brand: 'NIKE', model: 'VR Pro', flex: 'S' },
      { type: '4U', brand: 'NIKE', model: 'VR Pro', flex: 'S' },
